// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.4.22 <0.9.0;
import "./Identity.sol";

contract CVC {
    Identity[] ID;

    function createID() public{
        Identity id = new Identity();
        ID.push(id);
    }

    function getAddress(uint index) public view returns (address){
        return address(ID[index]);
    }

    function verifier(uint num) public pure returns (bytes4) {
        if (num%2==0){
            return "even";
        }
        else{
            return "odd";
        }
    }
}
